Example of reading and writing data: #include<pdi/context.h>
#include<pdi/paraconf_wrapper.h>
#include<pdi/plugin.h>
#include<pdi/ref_any.h>

structexample_plugin:publicPDI::Plugin
{
example_plugin(PDI::Context&ctx,PC_tree_tspec_tree):
Plugin{ctx}
{
ctx.callbacks().add_data_callback([](conststd::string&data_name,PDI::Refref){
if(PDI::Ref_rwref_rw{ref}){
//Plugincanreadandwrite
int*some_integer=ref_rw.get();
}elseif(PDI::Ref_rref_r{ref}){
//Plugincanread
constint*some_integer=ref_r.get();
}elseif(PDI::Ref_wref_w{ref}){
//Plugincanwrite
int*some_integer=ref_w.get();
}else{
//Plugincannotreadnorwrite
}
});
}
}

PDI_PLUGIN(example)

Specification tree: plugins:
example:
scalar:some_string
array:[0,1,2]
 Reading a scalar and an array: #include<pdi/context.h>
#include<pdi/paraconf_wrapper.h>
#include<pdi/plugin.h>
#include<pdi/ref_any.h>

structexample_plugin:publicPDI::Plugin
{
example_plugin(PDI::Context&ctx,PC_tree_tspec_tree):
Plugin{ctx}
{
//scalar
PC_tree_tscalar_tree=PC_get(spec_tree,".scalar");
std::stringstring_scalar=PDI::to_string(scalar_tree);

//array
PC_tree_tarray_tree=PC_get(spec_tree,".array");
intarray_size=PDI::len(array_tree);
std::vector<long>array;
for(inti=0;i<array_size;i++){
PC_tree_tarray_element=PC_get(array_tree,"[%d]",i);
array.emplace_back(PDI::to_long(array_element));
}
}
}

PDI_PLUGIN(example)

Specification tree: plugins:
