 Reading a scalar and an array: #include<unordered_map>

#include<pdi/context.h>
#include<pdi/paraconf_wrapper.h>
#include<pdi/plugin.h>
#include<pdi/ref_any.h>

structexample_plugin:publicPDI::Plugin
{
example_plugin(PDI::Context&ctx,PC_tree_tspec_tree):
Plugin{ctx}
{
PC_tree_tsubtree=PC_get(spec_tree,".custom_subtree");
intsubtree_size=PDI::len(subtree);
std::unordered_map<std::string,long>custom_map;

for(inti=0;i<subtree_size;i++){
PC_tree_tkey=PC_get(subtree,"{%d}",i);
PC_tree_tvalue=PC_get(subtree,"<%d>",i);
custom_map.emplace_back(PDI::to_string(key),PDI::to_long(value));
}
}
}

