print_number.c: #include<stdio.h>
#include<pdi.h>

voidprint_number(void)
{
void*input;PDI_access("input",&input,PDI_IN);
printf("I'vegotnumber%d.\n",*((int*)input));
PDI_release("input");
}

intmain(intargc,char*argv[])
{
PC_tree_tconf=PC_parse_path("print_number.yml");
PDI_init(conf);
intnumber=42;
PDI_share("number",&number,PDI_OUT);
PDI_event("print");
PDI_reclaim("number");
PDI_finalize();
return0;
}

