adding_to_number.c: #include<stdio.h>
#include<pdi.h>

voidadd_ten(void)
{
void*input;PDI_access("input",&input,PDI_OUT);
*((int*)input)+=10;
PDI_release("input");
}

intmain(intargc,char*argv[])
{
PC_tree_tconf=PC_parse_path("adding_to_number.yml");
PDI_init(conf);
intnumber=42;
printf("Beforeexpose,number=%d.\n",number);
PDI_expose("number",&number,PDI_IN);
printf("Afterexpose,number=%d.\n",number);
PDI_finalize();
return0;
}

calculate.c: #include<stdio.h>
#include<pdi.h>

voidsum_and_multiply(void)
{
void*number1;PDI_access("number1",&number1,PDI_IN);
void*number2;PDI_access("number2",&number2,PDI_IN);
void*sum;PDI_access("sum",&sum,PDI_OUT);
void*product;PDI_access("product",&product,PDI_OUT);
*((int*)sum)=*((int*)number1)+*((int*)number2);
*((int*)product)=*((int*)number1)**((int*)number2);
PDI_release("number1");
PDI_release("number2");
PDI_release("sum");
PDI_release("product");
}

intmain(intargc,char*argv[])
{
PC_tree_tconf=PC_parse_path("calculate.yml");
PDI_init(conf);
intfoo=4,bar=5,res1=0,res2=0;
printf("Beforecalculation,foo=%d,bar=%d,res1=%d,res2=%d.\n",foo,bar,res1,res2);
PDI_multi_expose("calculate",
"foo",&foo,PDI_OUT,
"bar",&bar,PDI_OUT,
"res1",&res1,PDI_IN,
"res2",&res2,PDI_IN,NULL);
printf("Aftercalculation,foo=%d,bar=%d,res1=%d,res2=%d.\n",foo,bar,res1,res2);
PDI_finalize();
return0;
}

